function renderReportChart(r,t){t=Object.fromEntries(Object.entries(t).reverse().slice(0,24).reverse());const e=[],o=[],a=[],d=[],n=[],i=[];for(const[r,l]of Object.entries(t))e.push(r.replaceAll("-","/")),o.push(l.totalShipping),a.push(l.totalFees),d.push(l.totalRefund),n.push(l.totalProfit),i.push(l.profitMargin);new Chart(r,{type:"bar",data:{labels:e,datasets:[{data:i,type:"line",yAxisID:"y2",label:"Gewinnmarge",pointRadius:6,pointHoverRadius:6,tension:.3,backgroundColor:"#fff",hoverBackgroundColor:"#fff",borderColor:"#000",borderWidth:2,hoverBorderWidth:2},{data:o,stack:"finances",label:"Versandkosten",backgroundColor:"#ffbb00",hoverBackgroundColor:"#ffbb00",borderWidth:0},{data:a,stack:"finances",label:"Gebühren",backgroundColor:"#fb6542",hoverBackgroundColor:"#fb6542",borderWidth:0},{data:d,stack:"finances",label:"Erstattungen",backgroundColor:"#698bbe",hoverBackgroundColor:"#698bbe",borderWidth:0},{data:n,stack:"finances",label:"Gewinn",backgroundColor:"#78ac4c",hoverBackgroundColor:"#78ac4c",borderWidth:0}]},options:{plugins:{legend:{position:"bottom",labels:{font:{size:15},boxWidth:24}},tooltip:{padding:{top:10,left:12,right:16,bottom:10},titleColor:"#000",bodyColor:"#000",backgroundColor:"#fff",borderColor:"#d5d6d7",borderWidth:1,cornerRadius:5,boxPadding:4,callbacks:{label:r=>`${r.dataset.label}: ${0==r.datasetIndex?formatPercent(r.raw/100):formatMoney(r.raw)}`}}},scales:{x:{stacked:!0,grid:{display:!1},border:{color:"#d5d6d7"}},y:{stacked:!0,beginAtZero:!0,ticks:{maxTicksLimit:8,callback:r=>formatMoney(r,0)},grid:{color:"#d5d6d7"},border:{dash:[5,5],color:"#d5d6d7"}},y2:{position:"right",min:0,max:100,ticks:{maxTicksLimit:6,callback:r=>formatPercent(r/100,0)},grid:{drawOnChartArea:!1,color:"#d5d6d7"},border:{color:"#d5d6d7"}}}}})}function renderMarginChart(r,t){new Chart(r,{type:"doughnut",data:{datasets:[{label:"Umsätze",data:[t.shippingRevenue,t.pickupRevenue],backgroundColor:["#2f88ff","#1060d6"],borderColor:"#fff",borderWidth:2},{label:"Kosten & Gewinn",data:[t.totalProfit,t.totalShipping,t.totalFees,t.totalRefund],backgroundColor:["#78ac4c","#ffbb00","#fb6542","#698bbe"],borderColor:"#fff",borderWidth:2},{label:"Profitmarge",data:[t.profitMargin,100-t.profitMargin],backgroundColor:["#000","rgba( 0 0 0 / 0 )"],borderColor:"#fff",borderWidth:2}]},options:{}})}document.addEventListener("DOMContentLoaded",(function(){Chart.defaults.responsive=!0,Chart.defaults.maintainAspectRatio=!1,Chart.defaults.offset=!0,Chart.defaults.clip=!1,Chart.defaults.layout.padding=6,Chart.defaults.animation=!1,Chart.defaults.interaction={mode:"index",intersect:!1},Chart.defaults.font.family="SUSE, sans-serif",Chart.defaults.font.size=14,Chart.defaults.font.weight=400,Chart.defaults.color="#000",document.querySelectorAll(".chart-container").forEach((r=>{const t=JSON.parse(r.querySelector(".chartdata").textContent),e=r.querySelector(".chart");switch(r.getAttribute("chart-type")){case"report":renderReportChart(e,t);break;case"margin":renderMarginChart(e,t);break}}))}));