document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".map-container").forEach((t=>{JSON.parse(t.querySelector(".mapdata").textContent);const e=t.querySelector(".map"),o=L.map(e).setView([51.163361,10.447683],6);o.attributionControl.setPrefix(!1),L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png",{attribution:"&copy; OpenStreetMap contributors"}).addTo(o)}))}));